import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import './SecretPage.css';

const SecretPage = () => {
  const { logout } = useAuth();
  const [currentDate, setCurrentDate] = useState(new Date());
  const [ddayCount, setDdayCount] = useState(0);
  const [videoData, setVideoData] = useState({});
  const [selectedDate, setSelectedDate] = useState(null);
  const [showVideoModal, setShowVideoModal] = useState(false);

  // D-day Í≥ÑÏÇ∞
  useEffect(() => {
    const targetDate = new Date('2025-11-08');
    const today = new Date();
    const diffTime = targetDate - today;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    setDdayCount(diffDays);
  }, []);

  // video.json Îç∞Ïù¥ÌÑ∞ Î°úÎìú
  useEffect(() => {
    fetch('/video.json')
      .then(response => response.json())
      .then(data => setVideoData(data))
      .catch(error => console.error('Error loading video data:', error));
  }, []);

  // Îã¨Î†• Í¥ÄÎ†® ÏÉÅÌÉú
  const [currentMonth, setCurrentMonth] = useState(new Date());

  const getDaysInMonth = (date) => {
    const year = date.getFullYear();
    const month = date.getMonth();
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const daysInMonth = lastDay.getDate();
    const startingDay = firstDay.getDay();

    return { daysInMonth, startingDay };
  };

  const goToPreviousMonth = () => {
    setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() - 1, 1));
  };

  const goToNextMonth = () => {
    setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() + 1, 1));
  };

  const formatDate = (year, month, day) => {
    return `${year}-${String(month + 1).padStart(2, '0')}-${String(day).padStart(2, '0')}`;
  };

  const handleDateClick = (day) => {
    const dateString = formatDate(currentMonth.getFullYear(), currentMonth.getMonth(), day);
    if (videoData[dateString]) {
      setSelectedDate(dateString);
      setShowVideoModal(true);
    }
  };

  const renderCalendar = () => {
    const { daysInMonth, startingDay } = getDaysInMonth(currentMonth);
    const days = [];

    // Ïù¥Ï†Ñ Îã¨Ïùò ÎßàÏßÄÎßâ ÎÇ†Îì§
    for (let i = 0; i < startingDay; i++) {
      days.push(<div key={`empty-${i}`} className="calendar-day empty"></div>);
    }

    // ÌòÑÏû¨ Îã¨Ïùò ÎÇ†Îì§
    for (let day = 1; day <= daysInMonth; day++) {
      const dateString = formatDate(currentMonth.getFullYear(), currentMonth.getMonth(), day);
      const hasVideo = videoData[dateString];

      days.push(
        <div 
          key={day} 
          className={`calendar-day ${hasVideo ? 'has-video' : ''}`}
          onClick={() => handleDateClick(day)}
        >
          {day}
          {hasVideo && <div className="video-indicator">üé•</div>}
        </div>
      );
    }

    return days;
  };

  const pairsData = [
    ['Ìö®ÏùÄ', 'ÏÑ±ÌòÑ', 'ÏßÄÏÑ≠', 'Îã§Ìòú', 'Ìö®Ïõê/ÎØºÏÑù', '', ''],
    ['ÏàòÏ†ï', 'Ïú†ÏÑ±', 'ÏÑ±Îπà', 'Îã§Ìòú/ÏïÑÏù∏', 'Ìö®Ïõê/ÎØºÏÑù', '', ''],
    ['ÏßÑÍ≤Ω', 'Ïö©ÌòÑ', 'ÏÑ±Îπà', 'Îã§Ìòú/ÏïÑÏù∏', 'Ìö®Ïõê/ÎØºÏÑù', '', ''],
    ['ÏùÄÏßÑ', 'Ïú†ÏÑ±/Ïö©ÌòÑ', 'ÏßÄÏÑ≠', 'Í∞ÄÏòÅ', 'Ìö®Ïõê/ÎØºÏÑù', '', '']
  ];

  const headers = ['ÏàúÌù¨', 'Ïú†Î†π', 'ÏòÅÏàò', 'ÏàúÏòÅ', 'ÎÇòÏÉÅÎ™®', 'Î©ÄÌã∞(ÎÇ®)', 'Î©ÄÌã∞(Ïó¨)'];

  const VideoModal = () => {
    if (!showVideoModal || !selectedDate) return null;

    const videos = videoData[selectedDate];
    const dateObj = new Date(selectedDate);
    const formattedDate = `${dateObj.getFullYear()}ÎÖÑ ${dateObj.getMonth() + 1}Ïõî ${dateObj.getDate()}Ïùº`;

    return (
      <div className="video-modal-overlay" onClick={() => setShowVideoModal(false)}>
        <div className="video-modal" onClick={(e) => e.stopPropagation()}>
          <div className="video-modal-header">
            <h3>{formattedDate} Ïó∞Ïäµ ÏòÅÏÉÅ</h3>
            <button 
              className="close-button" 
              onClick={() => setShowVideoModal(false)}
            >
              ‚úï
            </button>
          </div>
          <div className="video-list">
            {videos.map((video, index) => (
              <a
                key={index}
                href={video.url}
                target="_blank"
                rel="noopener noreferrer"
                className="video-link"
              >
                {video.text}
              </a>
            ))}
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="secret-page">
      <header className="dday-container">
        <div className="dday-title">üéØ Í≥µÏó∞ÍπåÏßÄ</div>
        <div className="dday-count">{ddayCount}Ïùº</div>
        <div className="dday-date">2025ÎÖÑ 11Ïõî 8Ïùº</div>
      </header>

      <div className="content">

        <div className="pairs-container">
          <h2 className="pairs-title">ÌéòÏñ¥ Î¶¨Ïä§Ìä∏</h2>
          <table className="pairs-table">
            <thead>
              <tr>
                {headers.map((header, index) => (
                  <th key={index}>{header}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {pairsData.map((row, rowIndex) => (
                <tr key={rowIndex}>
                  {row.map((cell, cellIndex) => (
                    <td key={cellIndex}>{cell}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        <div className="calendar-container">
          <div className="calendar-header">
            <button onClick={goToPreviousMonth}>&lt;</button>
            <div className="calendar-month">
              {currentMonth.getFullYear()}ÎÖÑ {currentMonth.getMonth() + 1}Ïõî
            </div>
            <button onClick={goToNextMonth}>&gt;</button>
          </div>
          <div className="calendar-grid">
            <div className="calendar-weekdays">
              <div>Ïùº</div>
              <div>Ïõî</div>
              <div>Ìôî</div>
              <div>Ïàò</div>
              <div>Î™©</div>
              <div>Í∏à</div>
              <div>ÌÜ†</div>
            </div>
            <div className="calendar-days">
              {renderCalendar()}
            </div>
          </div>
        </div>

        <button className="logout-button" onClick={logout}>
          Î°úÍ∑∏ÏïÑÏõÉ
        </button>
      </div>
      <VideoModal />
    </div>
  );
};

export default SecretPage;
